PickupHistory = PickupHistory or {}local function HUDPickupHistory()	local x = ScrW()/2	local y = ScrH()/1.6	for k,v in ipairs( PickupHistory ) do		if not v.fade then 			v.fade = CurTime() + 5		end		if not v.alpha then v.alpha = 255 end		if v.fade < CurTime() + 2 then			v.alpha = v.alpha - 64 / 20;			v.alpha = math.Clamp( v.alpha, 0, 255 );		end		if k > 0 then 			y = y + 32		end		if v.alpha ~= 0 then			DrawShadowText( "You picked up a "..v.weap, "GM_Med2Numbers", x, y, Color(255,255,255,v.alpha), 1, 0 )		else			table.remove(PickupHistory,k)		end	endendhook.Add("HUDPaint","PickupHistory",HUDPickupHistory)net.Receive( "GM_Pickup", function( length )	local classname = getLanguage( net.ReadString() )	local weapon = {weap = classname}	table.insert( PickupHistory, weapon )end)